    EMP_JOBID=#{jobid},EMP_SAL=#{salary},EMP_SOSECUR=#{socialSecurity},EMP_TRAINID=#{trainid},EMP_PHONE=#{phone},
    EMP_EMAIL=#{email},EMP_STATE=#{state} where EMP_ID=#{id}
